import React, { Component } from "react";
import axios from "axios";
import BackToHome from "../components/backToHome";

class UsersListWithAxios extends Component {
  static async getInitialProps() {
    const { data: userData } = await axios.get(
      "https://reqres.in/api/users?per_page=12"
    );

    return {
      userList: userData.data
    };
  }
  render() {
    return (
      <React.Fragment>
        <div className="pricing-header px-3 py-3 pt-md-5 pb-md-4 mx-auto text-center">
          <BackToHome />
          <h1 className="display-4">Axios Fetch</h1>
          <p className="lead">
            The below data is fetched from
            <a href="https://reqres.in/" target="_blank">
              https://reqres.in/
            </a>
            <br />
            using Axios Get and
            <a
              href="https://nextjs.org/docs#fetching-data-and-component-lifecycle"
              target="_blank"
            >
              getInitialProps
            </a>
            .
          </p>
        </div>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Full name</th>
              <th scope="col">Email</th>
            </tr>
          </thead>
          <tbody>
            {this.props.userList.map(e => (
              <tr key={e.id}>
                <th scope="row">{e.id}</th>
                <td>
                  <img src={e.avatar} />
                  <style jsx>{`
                    img {
                      width: 45px;
                      height: 45px;
                      display: inline-block;
                      margin-right: 10px;
                    }
                  `}</style>
                  {e.first_name + " " + e.last_name}
                </td>
                <td>{e.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default UsersListWithAxios;
