import React, { Component } from "react";
import fetch from "isomorphic-unfetch";

class UsersListWithFetch extends Component {
  static async getInitialProps() {
    const res = await fetch("https://reqres.in/api/users?per_page=12");
    //const errorCode = res.statusCode > 200 ? res.statusCode : false;
    const json = await res.json();

    return {
      userList: json.data
    };
  }
  render() {
    return (
      <React.Fragment>
        <div className="pricing-header px-3 py-3 pt-md-5 pb-md-4 mx-auto text-center">
          <h1 className="display-4">Isomorpheic Fetch</h1>
          <p className="lead">
            The below data is fetched from https://reqres.in/ using Isomorpheic
            Fetch npm and getInitialProps.
          </p>
        </div>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Full name</th>
              <th scope="col">Email</th>
            </tr>
          </thead>
          <tbody>
            {this.props.userList.map(e => (
              <tr>
                <th scope="row">{e.id}</th>
                <td>
                  <img src={e.avatar} />
                  {e.first_name + " " + e.last_name}
                </td>
                <td>{e.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default UsersListWithFetch;
